data.logic:
var calc_output = [];

//user inputs & addition of selected points
var score = parseFloat(hands) + parseFloat(rigidity) + parseFloat(posture) + parseFloat(upper) + parseFloat(gait) + parseFloat(tremor) + parseFloat(facies) + parseFloat(sebor) + parseFloat(speech) + parseFloat(selfc);

//interpretation
if(score < 11){
	var dis ='Early illness';
    var minidis = 'Early';
}else if(10 < score < 21){
	var dis = 'Moderate disability';
    var minidis = 'Moderate';
}
if(20 < score){
    var dis = 'Severe disability';
    var minidis = 'Severe';
}


//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: minidis
});
calc_output.push({
    name: 'Webster Rating',
    value: score,
    value_text: 'points',
    message: dis
});
