data.logic:
var calc_output = [];

//user inputs

var sit1 = parseFloat(sit1);
var sit2 = parseFloat(sit2);
var sit3 = parseFloat(sit3);
var dy1 = parseFloat(dy1);
var dy2 = parseFloat(dy2)
var dy3 = parseFloat(dy3);
var dy4 = parseFloat(dy4);
var dy5 = parseFloat(dy5);
var dy6 = parseFloat(dy6);
var dy7 = parseFloat(dy7);
var dy8 = parseFloat(dy8);
var dy9 = parseFloat(dy9);
var dy10 = parseFloat(dy10);
var co1 = parseFloat(co1);
var co2 = parseFloat(co2);
var co3 = parseFloat(co3);
var co4 = parseFloat(co4);

var sitScore = sit1 + sit2 +sit3;
if(!sitScore){
	sitScore = 0;
}

var dyScore = (dy1 ? 1 : 0) + (dy2 ? 1 : 0) + (dy3 ? 1 : 0) + (dy4 ? 1 : 0) + (dy5 ? 1 : 0) + (dy6 ? 1 : 0) + (dy7 ? 1 : 0) + (dy8 ? 1 : 0) + (dy9 ? 1 : 0) + (dy10 ? 1 : 0);

var coScore = co1 + (co2 ? 1 : 0) + co3 + (co4 ? 1 : 0);
if(!coScore > 0){
    coScore = coScore ? 1 : 0;
}

var tis = sitScore + dyScore + coScore;

//results
calc_output.push({
    name: 'mini',
    value: tis,
    value_text: '',
    message: 'Total Trunk Impairment Scale'
});
calc_output.push({
    name: 'Trunk Scale',
    value: tis,
    value_text: '',
    message: 'Total Trunk Impairment Scale'
});
calc_output.push({
    name: 'Breakdown',
    value: '',
    value_text: '',
    message: 'Total static sitting balance: ' + sitScore + '<br/>Total dynamic sitting balance: ' + dyScore + '<br/>Total coordination: ' + coScore
});
