data.logic:
var calc_output = [];

//user inputs - addition of selected points
var cyto = parseFloat(cyto);
var med = parseFloat(med);
var hemo = parseFloat(hemo);
var plat = parseFloat(plat);
var anc = parseFloat(anc);

var score = cyto + med + hemo + plat + anc; 

//interpretation
var risk, survival, evolution;
if(score < 1.6){
	risk = 'Very low';
	survival = '8.8';
	evolution = 'not reached';
}
if(1.5 < score && score < 4){
	risk = 'Low';
	survival = '5.3';
	evolution = '10.8 years';
}
if(3 < score && score <= 4.5){
	risk = 'Intermediate';
	survival = '3';
	evolution = '3.2 years';
}
if(4.5 < score && score <= 6){
	risk = 'High';
	survival = '1.6';
	evolution = '1.4 years';
}
if(6 < score){
	risk = 'Very high';
	survival = '0.8';
	evolution = '0.73 years';
}


//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk'
});
calc_output.push({
    name: 'IPSS R Score',
    value: score,
    value_text: 'points',
    message: 'IPSS-R Score'
});
calc_output.push({
    name: 'IPSS R Score',
    value: risk,
    value_text: 'risk',
    message: ''
});
calc_output.push({
    name: 'IPSS R Score',
    value: survival,
    value_text: 'years',
    message: 'Median survival <br/><br/>' + 'Median time to 25% AML evolution: '+ evolution
});
