data.logic:
var calc_output = [];

//user inputs - addition of selected points
var score = parseFloat(alert) + parseFloat(amt) + parseFloat(atte) + parseFloat(acute);

//interpretation
var msg, minimsg;
if(score >= 4){
	msg = 'Possible delirium and/or cognitive impairment';
    minimsg = 'Delirium';
}
if(score > 0 && score < 4){
	msg = 'Possible cognitive impairment';
    minimsg = 'Cognition impaired';
}
if(score === 0){
	msg = 'Delirium or severe cognitive impairment unlikely. Note that delirium is still possible if "acute change or fluctuating course" is questionable.';
    minimsg = 'Unlikely';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: minimsg
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: msg
});
