data.logic:
var calc_output = [];

if (UOMSYSTEM == true) {
	weight = weight * 28.3495 / 1000
}
else {
	weight = weight / 1000
}

hct_initial = parseFloat(hct_initial) / 100
hct_goal = parseFloat(hct_goal) / 100

var term = parseFloat(term)
var volume

if (term == 0) {
  volume = 100 * weight
}
else {
  volume = 85 * weight
}

var vol = parseFloat(volume) * (hct_initial - hct_goal) / hct_initial

if (hct_initial <= hct_goal) {
calc_output.push({
   name: 'mini',
   value: '',
   value_text: '',
   message: 'Goal hematocrit should be less than initial'
});
calc_output.push({
   name: '10006_Neonatal Partial Exchange_result',
   value: '',
   value_text: '',
   message: 'Goal hematocrit should be less than initial. Please re-check values.'
});
}

else {
calc_output.push({
   name: 'mini',
   value: vol.toFixed(0),
   value_text: 'mL',
   message: 'Replacement fluid volume'
});
calc_output.push({
   name: '10006_Neonatal Partial Exchange_result',
   value: vol.toFixed(0),
   value_text: 'mL',
   message: 'Replacement fluid volume'
});
calc_output.push({
   name: '10006_Neonatal Partial Exchange_interpretation',
   value: '',
   value_text: '',
   message: 'Remove ' + vol.toFixed(0) + ' mL ' + 'whole blood, replace ' + vol.toFixed(0) + ' mL ' + 'crystalloid (blood volume = ' + volume.toFixed(0) + ' mL)'
});
}
