data.logic:
var calc_output = [];

//user inputs
var map = parseFloat(map);
var icp = parseFloat(icp);

var cpp = map - icp;

var risk = 'Within normal limits'
var rec = 'Likely adequate perfusion.'
if (cpp < 60){
	risk = 'Low CPP'
	rec = 'Consider interventions to increase MAP or decrease ICP (see Next Steps for details).'
}
if (cpp > 70){
	risk = 'High CPP'
	rec = 'Consider weaning pressors if MAP is adequate (see Next Steps for details).'
}

//results
calc_output.push({
    name: 'mini',
    value: cpp,
    value_text: 'mmHg',
    message: risk
});
calc_output.push({
    name: '3985_CPP_result',
    value: cpp,
    value_text: 'mmHg',
    message: risk + '. ' + rec
});
