var calc_output = [];

//user inputs
var classm = parseFloat(killip);

var classRN, mort;

if(classm === 1){
	classRN = 'Class I';
	mort = '2-3';
}
if(classm === 2){
	classRN = 'Class II';
	mort = '5-12';
}
if(classm === 3){
	classRN = 'Class III';
	mort = '10-20';
}
if(classm === 4){
	classRN = 'Class IV';
	mort = '10-20';
}

//results
calc_output.push({
    name: 'mini',
    value: classRN,
    value_text: '',
    message: mort + '% 30-day mortality'
});
calc_output.push({
    name: 'Killip Classification',
    value: classRN,
    value_text: '',
    message: 'Killip Classification'
});
calc_output.push({
    name: 'Mortality',
    value: mort,
    value_text: '%',
    message: '30-day mortality'
});
