data.logic:
var calc_output = [];

//user inputs
var duration = parseFloat(duration);
var stseg = parseFloat(stseg);
var index = parseFloat(index);

var score = duration - (5*stseg) - (4*index);
var risk, survival;
var rec;

if(score >= 5){
	risk = 'Low';
	survival = '99%'
    rec = ''
}
if(score < 5 && score >= -10){
	risk = 'Medium';
	survival = '95%'
    rec = ''
}
if(score < -10){
	risk = 'High';
	survival = '79%'
    rec = '. Coronary angiography recommended (see Next Steps).'
}
if(score > 0){
    score = '+' + score
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: '',
    message: risk + ' risk' + rec
});
calc_output.push({
    name: 'Duke Treadmill Score',
    value: score,
    value_text: '',
    message: 'Duke Treadmill Score'
});
calc_output.push({
    name: 'Risk Group & Survival',
    value: risk,
    value_text: 'risk',
    message: survival + ' survival at 5 years' + rec
});
