data.logic:
var calc_output = [];

//user inputs
var type = parseFloat(type);
var inputs = parseFloat(inputs);

if(type == 0){	//for diagnosis
	if (inputs == 0 ){
		var A = parseFloat(truePosZ);
		var B = parseFloat(falsePosZ);
		var C = parseFloat(falseNegZ);
		var D = parseFloat(trueNegZ);

		var preval = (A + C)/(A + B + C + D);
		var sens = A / (A + C);
		var spec = D / (B + D);

		var pos_pred_value = (100*(A / (A + B)).toFixed(1)).toString()+ '%';		//PPV / Positive predictive value, % = 100 × A / (A + B)
		var neg_pred_value = (100*(D / (C + D)).toFixed(1)).toString()+ '%';		//NPV / Negative predictive value, % = 100 × D / (C + D)*/

		var neg_like_ratio = (1-sens)/spec;	//Negative likelihood ratio = (1 – sensitivity) / specificity
		var pos_like_ratio = (sens) / (1-spec);		//Positive likelihood ratio = Sensitivity / (1 – specificity)
	}

	if (inputs == 1){
		var preval = parseFloat(preval);
		var sens = parseFloat(sens);
		var spec = parseFloat(spec);

		var pos_pred_value = ((((100*(sens*preval)/((sens*preval)+(100 - spec)*(100 - preval)))))).toFixed(1).toString()+ '%';
		var neg_pred_value = ((100*spec*(100 - preval)/((100 - sens)*preval + spec*(100 - preval))).toFixed(1)).toString()+ '%';

		var neg_like_ratio = (100-sens)/spec;	//Negative likelihood ratio = (1 – sensitivity) / specificity
		var pos_like_ratio = (sens) / (100-spec);		//Positive likelihood ratio = Sensitivity / (1 – specificity)
	}

	var pre_test_odds = preval / (100-preval); 	//Pre-test odds = Prevalence / (1 – prevalence)
	var post_test_odds = pre_test_odds*pos_like_ratio; 	//Post-test odds = Prevalence × likelihood ratio

	var pre_test_prob = ((preval).toFixed(1)).toString() + '%';							//Pre-test probability = prevalence
	var post_test_prob = ((100*(post_test_odds) / (1+post_test_odds)).toFixed(1)).toString() + '%';	//Post-test probability = Post-test odds / (1 + post-test odds)

	//results
	calc_output.push({
	    name: 'mini',
	    value: null,
	    value_text: '',
	    message: 'Tap arrow to see stats'
	});
	calc_output.push({
	    name: 'Likelihood Ratio',
	    value: null,
	    value_text: '',
	    message: 'LR- = ' + (neg_like_ratio).toFixed(1) + '<br/>LR+ = ' + (pos_like_ratio).toFixed(1)
	});
	calc_output.push({
	    name: 'Probability',
	    value: null,
	    value_text: '',
	    message: 'Pre-test probability = ' + pre_test_prob + '<br/>Post-test probability = ' + post_test_prob
	});
	calc_output.push({
	    name: 'Predictive Value',
	    value: null,
	    value_text: '',
	    message: 'PPV = ' + pos_pred_value + '<br/>NPV = ' + neg_pred_value
	});
	if(inputs === 0){
		calc_output.push({
	    name: 'prev sens spec',
	    value: null,
	    value_text: '',
	    message: 'Prevalence = ' + (100*preval).toFixed(1) + '%<br/>Sensitivity = ' + (100*sens).toFixed(1) + '%<br/>Specificity = ' + (100*spec).toFixed(1) + '%'
	});
	}
}

if (type == 1){		//for treatment
	var A = parseFloat(truePos);
	var B = parseFloat(falsePos);
	var C = parseFloat(falseNeg);
	var D = parseFloat(trueNeg);

	var risk_of_outcome_exp = (A / (A + B))*100;	//EER	//Risk of outcome (absolute risk), experimental group* = A / (A + B)
	var risk_of_outcome_con = (C / (C + D))*100;	//CER	//Risk of outcome (absolute risk), control group** = C / (C + D)

	var rel_risk = risk_of_outcome_exp/risk_of_outcome_con;		//Relative risk, RR = EER / CER
	var odds_ratio = (A / B) / (C / D);							//Odds ratio, OR = (A / B) / (C / D)

	var abs_risk_reduc = risk_of_outcome_exp-risk_of_outcome_con; 	//Absolute risk reduction, ARR = EER – CER
	var num_needed = 100/abs_risk_reduc;						//Number needed to treat, NNT = 1 / ARR
	var rel_risk_reduc = 100*(abs_risk_reduc/risk_of_outcome_con);		//Relative risk reduction, RRR = ARR / CER

	//results
	calc_output.push({
	    name: 'mini',
	    value: null,
	    value_text: '',
	    message: 'Tap arrow to see stats'
	});
	calc_output.push({
	    name: 'Risk of outcome',
	    value: null,
	    value_text: '',
	    message: 'EER = ' + ((risk_of_outcome_exp).toFixed(1)).toString() + '%' + '<br/>CER = ' + ((risk_of_outcome_con).toFixed(1)).toString() + '%'
	});
	calc_output.push({
	    name: 'Risk',
	    value: null,
	    value_text: '',
	    message: 'RR = ' + (rel_risk).toFixed(1) + '<br/>OR = ' + (odds_ratio).toFixed(1)
	});
	calc_output.push({
	    name: 'Probability',
	    value: null,
	    value_text: '',
	    message: 'ARR = ' + (abs_risk_reduc).toFixed(1) + '%' + '<br/>RRR = ' + (rel_risk_reduc).toFixed(1) + '%' + '<br/>NNT = ' + (num_needed).toFixed(1)
	});
}
