data.logic:
var calc_output = [];

//user inputs
var score = parseFloat(ecog) + parseFloat(stress) + parseFloat(copd) + parseFloat(cvd) + parseFloat(nci) + parseFloat(mono);

var risk, risk2, comp, rec;
if(score === 0){
	risk = 'I';
	risk2 = 'Low';
	comp = '1.1%';
	rec = 'Consider discharge with oral antibiotics after discussion with oncology.';
}else if(score === 1 || score ===2 ){
	risk = 'II';
	risk2 = 'Intermediate';
	comp = '6.2%';
	rec = 'Use clinical judgment regarding admission. Consider oncology consultation.';
}else if(score >= 3){
	risk = 'III';
	risk2 = 'High';
	comp = '36%';
	rec = 'Admit for further investigation, including blood cultures.';
}



//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk2
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: rec
});
calc_output.push({
    name: 'Result',
    value: risk2,
    value_text: 'risk',
    message: 'Risk category ' + risk +'<br/><br/>'+ comp + ' risk of complications within a minimum of 7 days (hypotension, organ failure, etc.—see Facts & Figures for details)'
});
