data.logic:
var calc_output = [];

//user inputs
var score = parseFloat(asy) + parseFloat(age) + parseFloat(wbc) + parseFloat(hr);

//results
var rec;
var msg;

if(score >=0 && score <3){
	rec = "Reassess";
  	msg = '>83.3% likelihood of pseudocellulitis';
}else if(score === 3 || score === 4){
	rec = "Consult";
  	msg = 'Dermatology and/or infectious disease consultation'
}else if(score > 4){
	rec = "Treat";
  	msg = '>82.2% likelihood of true cellulitis'
}

var pt = 'points';
if(score === 1){
	pt = 'point';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: rec
});
calc_output.push({
    name: 'Result',
    value: score,
    value_text: pt,
    message: 'ALT-70 Score'
});
calc_output.push({
    name: 'Recommendation',
    value: rec,
    value_text: '',
    message: msg
});
