data.logic:
var calc_output=[]; 
var msg='';
var mini_msg=''; 
if(parseFloat(stroke)==2){
	msg+='Note: While history of stroke provides 2 points, most physicians would move these patients directly to the high risk group (>8.5% risk of event per year if no coumadin.) <br/><br/>By points directly:<br/>'
} 
var u=parseFloat(heart_failure)+parseFloat(hypertension)+parseFloat(age_75)+parseFloat(diabetes)+parseFloat(stroke); 
if(u==0){
	msg+='Low risk of thromboembolic event. 1.9% risk of event per year if no coumadin.'; mini_msg='1.9% risk of event per year.'
} 
if(u==1){
	msg+='Intermediate risk of thromboembolic event. 2.8% risk of event per year if no coumadin.'; 
	mini_msg='2.8% risk of event per year.'
} 
if(u==2){
	msg+='Intermediate risk of thromboembolic event. 4.0% risk of event per year if no coumadin.'; mini_msg='4.0% risk of event per year.'
} 
if(u==3){
	msg+='High risk of thromboembolic event. 5.9% risk of event per year if no coumadin.'; mini_msg='5.9% risk of event per year.'
} 
if(u==4){
	msg+='High risk of thromboembolic event. 8.5% risk of event per year if no coumadin.'; mini_msg='8.5% risk of event per year.'
} 
if(u==5){
	msg+='High risk of thromboembolic event. 12.5% risk of event per year if no coumadin.'; mini_msg='12.5% risk of event per year.'
} 
if(u==6){
	msg+='High risk of thromboembolic event. 18.2% risk of event per year if no coumadin.'; mini_msg='18.2% risk of event per year.'
} 
msg+='<br/><br/>The adjusted stroke rate was the expected stroke rate per 100 person-years derived from the multivariable model assuming that aspirin was not taken.';

calc_output.push({
	name:'mini',
	value:u.toFixed(0),
	value_text:'points',
	message:mini_msg
}); 
calc_output.push({
	name:'40_CHADS2 Score_result',
	value:u.toFixed(0),
	value_text:'points',
	message:msg
});
