data.logic:
var calc_output = [];

//user inputs
var score = parseFloat(bp) + parseFloat(trop) + parseFloat(rv) + parseFloat(hr);

//interpretation
var stage, comp, mort, risk;
if(score < 3){
	stage = 'I';
	comp = '4.4';
	mort = '3.1';
    risk = 'Low';

}else if(score < 5){
	stage = 'II';
	comp = '18';
	mort = '6.8';
    risk = 'Intermediate';

}else{
	stage = 'III';
	comp = '42';
    risk = 'High';
    mort = '10';
} 

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Bova Score'
});
calc_output.push({
    name: 'Stage',
    value: 'Stage ' + stage,
    value_text: '',
    message: risk + ' risk'
});
calc_output.push({
    name: 'PE',
    value: comp,
    value_text: '%',
    message: 'PE-related complications (death from PE, hemodynamic collapse, or recurrent nonfatal PE) at 30 days<br/><br/>'+mort+'% PE-related mortality at 30 days'
});
