data.logic:
var calc_output = [];

//user inputs
var score = parseFloat(post) + parseFloat(level) + parseFloat(bmi) + parseFloat(age);

//interpretation
var rec, risk, risk_grp;
if(score<2){
	rec = 'Can safely discontinue oral anticoagulation';
    risk = '3.0'
    risk_grp = 'Low'
}else{
	rec = 'Continue oral anticoagulation';
    risk = '7.4'
    risk_grp = 'Not low'
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: rec
});
calc_output.push({
    name: 'score',
    value: score,
    value_text: 'points',
    message: 'HERDOO2 Score'
});
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: '%',
    message: 'Risk of recurrent major VTE per 100 patient years'
});
calc_output.push({
    name: 'risk group',
    value: risk_grp,
    value_text: 'risk',
    message: rec
});
