data.logic:
var calc_output = [];

if (UOMSYSTEM == true) {
	var bilirubin = parseFloat(bilirubin)*17.1;
	var albumin = parseFloat(albumin)*10;

}if (UOMSYSTEM == false) {
 	var bilirubin = parseFloat(bilirubin);
 	var albumin = parseFloat(albumin);
}

var albi = (Math.log10(bilirubin)*0.66) + (albumin*(-0.085));
var grade = ''
var survival = ''

if (albi.toFixed(2) <= -2.60){
	grade = '1' 
  	survival = '18.5-85.6'
}
else if (albi.toFixed(2) > -2.60 && albi.toFixed(2) <= -1.39) {
	grade = '2' 
  	survival = '5.3-46.5'
}
else if (albi.toFixed(2) > -1.39) {
	grade = '3' 
  	survival = '2.3-15.5'
}

calc_output.push({
    name: 'mini',
    value: 'Grade ' + grade,
    value_text: '',
    message: survival + ' months median survival'
});

calc_output.push({
   name: 'albi',
   value: albi.toFixed(2),
   value_text: 'points',
   message: 'ALBI Score'
});

calc_output.push({
   name: 'grade',
   value: 'Grade ' + grade,
   value_text: '',
   message: ''
});
calc_output.push({
   name: 'survival',
   value: survival,
   value_text: 'months',
   message: 'Median survival'
});
