data.logic:
var calc_output = [];

//user inputs
var total = parseFloat(related) + parseFloat(frequency) + parseFloat(form);

var result, min, msg

//interpretation
	result = "Negative";
	min = "Unikely IBS.";
	msg = ' Consider further assessment for other pathology.'

if(total >= 2){
	result = 'Positive';
	min = 'Likely IBS.';
	msg = ''
}

//results
calc_output.push({
    name: 'mini',
    value: result,
    value_text: '',
    message: min
});
calc_output.push({
    name: 'Result',
    value: result,
    value_text: '',
    message: min + msg
});

if (total >= 2){
var sub = parseFloat(subtype);
var type, def;
	if(sub == 0){
		type = 'IBS-C';
		def = 'IBS with predominant constipation';
	}else if(sub == 1){
		type = 'IBS-D';
		def = 'IBS with predominant diarrhea';
	}else if(sub == 2){
		type = 'IBS-M';
		def = 'IBS with mixed bowel habits';
	}else if(sub == 3){
		type = 'IBS-U';
		def = 'Unclassified IBS';
	}
	calc_output.push({
	    name: 'Sub',
	    value: type,
	    value_text: '',
	    message: def
	});
}
