data.logic:
var calc_output = [];

//user inputs - addition of correct points
var score = parseFloat(gaze) + parseFloat(instruct);
//Incorrectly answers ≥1 of two “level of consciousness” questions on NIHSS (age or current month) AND does not follow ≥1 of the two commands (close eyes, open/close hand)
var ask = parseFloat(ask);
var close = parseFloat(close);
if(ask > 0 && close > 0){
	score = score + 1;
}

//interpretation
var i, rec;
if(score < 2){
	i = 'LVO and NIHSS ≥15 less likely.';
    rec = ''
}
else {
    i = 'LVO and NIHSS ≥15 likely. ';
    rec = 'Consider initiating full workup and treatment for LVO as appropriate, including CT angiogram (see Management for details).'
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: i
});
calc_output.push({
    name: 'Result',
    value: score,
    value_text: 'points',
    message: i + rec
});
