data.logic:
var calc_output = [];

//user inputs
var initial = parseFloat(bmi) + parseFloat(weight) + parseFloat(week) + parseFloat(icu);
var risk = "Low risk";
var rec = "Re-screen weekly";
var minirec = "Re-screen weekly";

if(initial > 0){
	var score = parseFloat(status) + parseFloat(severity) + parseFloat(age);
	if(score >= 0 && score <= 3){
		risk = "Low risk";
		rec = "Re-screen weekly"
        minirec = "Re-screen weekly"
	} else if(score == 4){
		risk = "At risk";
		rec = "Initiate nutritional care plan (food, oral supplements, tube feeding, and/or parenteral nutrition as appropriate)"
        minirec = "Initiate nutritional care plan"
	} else if(score >= 5 && score <= 7){
		risk = "High risk";
		rec = "Initiate early intervention nutritional care plan"
        minirec = "Initiate early intervention nutritional care plan"
	}

}

//results
calc_output.push({
    name: 'mini',
    value: risk,
    value_text: '',
    message: minirec
});
if(initial > 0){
calc_output.push({
    name: 'Risk',
    value: score,
    value_text: 'points',
    message: 'NRS-2002'
});
}
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: '',
    message: rec
});
