data.logic:
var calc_output = [];

//user inputs
var bap = parseFloat(bun) + parseFloat(ams) + parseFloat(pulse);

var clas, mort, mv, agescore;
if(bap == 0){
	if(parseFloat(age) == 0){
		clas = 'Class I';
		mort = '0.3';
		mv = '0.3%';
	}else{
		clas = 'Class II';
		mort = '1.0';
		mv = '0.2%';
	}	
}else if(bap === 1){
	clas = 'Class III';
	mort = '2.2';
	mv = '1.2%';
}else if(bap === 2){
	clas = 'Class IV';
	mort = '6.4';
	mv = '5.5%';
}else{
	clas = 'Class V';
	mort = '14.1';
	mv = '12.4%';
}

if (clas == 'Class I'){
	agescore = ', age <65 years'
}
else if (clas == 'Class II'){
	agescore = ', age ≥65 years'
}
else {
	agescore = ''
}

var rec = 'Consider early non-invasive ventilation and/or ICU care';
if(clas === 'Class I' || clas === 'Class II'){
	rec = 'Routine management of COPD exacerbation';
}

//results
calc_output.push({
    name: 'mini',
    value: clas,
    value_text: '',
    message: mort + '% in-hospital mortality'
});
calc_output.push({
    name: 'Class',
    value: clas,
    value_text: '',
    message: 'BAP ' + bap + agescore + '<br/><br/>' + rec
});
calc_output.push({
    name: 'Mort',
    value: mort,
    value_text: '%',
    message: 'In-hospital mortality<br/><br/> ' + mv + ' requiring intubation within 48 hrs<br/><br/>' 
});
