data.logic:
var calc_output = [];

var u = parseFloat(age) + parseFloat(knowndisease) + parseFloat(exercisepain) + parseFloat(reproduciblepain) + parseFloat(cardiacpain);
var risk;
var msg;
var mini_msg;

// define risk


if (u == 0 || u == 1 || u == 2) {
	risk = '3'; 
	msg = 'Outpatient evaluation as needed.'
    mini_msg = 'Non-urgent evaluation.'
}

if (u >= 3) {
	risk = '23'; 
	msg = 'Consider urgent evaluation or inpatient admission.'
    mini_msg = 'Urgent evaluation.'
}

// result on app
calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: mini_msg
});

// result on web - column 1
calc_output.push({
    name: 'score',
    value: u,
    value_text: 'points',
    message: 'Marburg Heart Score'
});
// result on web - column 2
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: '%',
    message: 'CAD risk. ' + msg 
});
