data.logic:
var calc_output = [];

var bolus = parseFloat(bolus)

var rate = 2 / 3 * bolus
var halfbolus = bolus / 2

calc_output.push({
    name: 'mini',
    value: rate.toFixed(1),
    value_text: 'mg/hr',
    message: 'IV drip rate'
});
calc_output.push({
    name: 'rate',
    value: rate.toFixed(1),
    value_text: 'mg/hr',
    message: 'Continuous IV infusion starting rate'
});
calc_output.push({
    name: 'additional',
    value: halfbolus.toFixed(1),
    value_text: 'mg',
    message: 'May give ½ bolus dose 15 mins after starting drip'
});
