data.logic:
var calc_output = [];

var breathing = parseFloat(breathing)
var movement = parseFloat(movement)
var tone = parseFloat(tone)
var volume = parseFloat(volume)

var test 

if (breathing + movement + tone + volume == 8) {
    var test_normal = parseFloat(test_normal)
    if (test_normal == 3) {
        test = 0
    } else {
        test = test_normal
    }
}
else {
    test = parseFloat(test)
}

var u = breathing + movement + tone + volume + test
var interpretation;
var msg;
var mini_msg;

// normal and NST not done (8/8)
if (u > 10) {
    u = 8
    interpretation = 'Normal, non-asphyxiated fetus (sonography normal, NST omitted)'; 
    msg = 'No fetal indication for intervention; repeat test weekly except in diabetic patients and post-term pregnancy (twice weekly)'
    mini_msg = 'No intervention'
    }
// normal and NST done (10/10)
else if (u == 10) {
    interpretation = 'Normal, non-asphyxiated fetus'; 
    msg = 'No fetal indication for intervention; repeat test weekly except in diabetic patients and post-term pregnancy (twice weekly)'
    mini_msg = 'No intervention'
}
// 8/10 
else if (u == 8) {
    if (volume == 2) { // normal AFV
    interpretation = 'Normal, non-asphyxiated fetus (normal AFV)'; 
    msg = 'No fetal indication for intervention; repeat testing per protocol'
    mini_msg = 'Repeat testing'
    }
    else if (volume == 0) { // abnormal AFV
    interpretation = 'Chronic fetal asphyxia suspected (decreased AFV)'; 
    msg = 'Delivery recommended'
    mini_msg = 'Delivery recomended'
    }
}
// 6 Normal or abnormal AFV
if (u == 6) {
    if (volume == 2) { // normal AFV
        interpretation = 'Possible fetal asphyxia, normal AFV'; 
        msg = 'Delivery recommended if >36 weeks with favorable cervix or repeat test ≤6; if repeat test >6, observe and repeat per protocol'
        mini_msg = 'Delivery or repeat testing recommended'
    }
    else if (volume == 0) { // abnormal AFV
        interpretation = 'Possible fetal asphyxia, abnormal AFV'
        msg = 'Delivery recommended'
        mini_msg = 'Delivery recomended'
    }
}

if (u == 4) {
    interpretation = 'Probable fetal asphyxia'; 
    msg = 'Repeat testing same day; if ≤6, delivery recommended'
    mini_msg = 'Repeat testing'
}

if  (u <= 2) {
    interpretation = 'Almost certain fetal asphyxia'; 
    msg = 'Delivery recomended'
    mini_msg = 'Delivery recomended'
}

// result on app
calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: mini_msg
});

// result on web - column 1
calc_output.push({
    name: 'score',
    value: u,
    value_text: 'points',
    message: interpretation
});
// result on web - column 2
calc_output.push({
    name: 'interpretation',
    value: '',
    value_text: '',
    message: msg 
});
