data.logic:
var calc_output = [];
if (UOMSYSTEM == false) {
  glucose = parseFloat(glucose) / 0.0555;
}
var weight = parseFloat(weight)
var glucose = parseFloat(glucose)
var dextrose = parseFloat(dextrose)

var ins_bolus = weight
var ins_infusion = (weight/2).toFixed(0) + '-' + weight.toFixed(0) 
var dex_bolus = weight.toFixed(0) + '-' + (2 * weight).toFixed(0)
var glu_msg = ' (since serum glucose is <250 mg/dL or 13.9 mmol/L)' 
var dex_infusion = weight/2  
var dex_msg = 'D50'

if (dextrose == 1) {
    dex_infusion = weight
    dex_msg = 'D25'
}
else if (dextrose == 2) {
    dex_infusion = 2.5 * weight
    dex_msg = 'D10'
}

var msgAppend = '<br/><br/>' + dex_bolus + ' cc D50 bolus' + glu_msg

if (glucose >= 250) {
    msgAppend = ''
}



calc_output.push({
    name: 'mini',
    value: ins_bolus.toFixed(0),
    value_text: 'units',
    message: 'Regular insulin bolus'
}); 
calc_output.push({
    name: 'insulin bolus',
    value: ins_bolus.toFixed(0),
    value_text: 'units',
    message: 'Regular insulin bolus' + msgAppend
}); 
calc_output.push({
    name: 'insulin infusion',
    value: ins_infusion,
    value_text: 'units/hr',
    message: 'Regular insulin continuous infusion'
});
calc_output.push({
    name: 'dextrose infusion',
    value: dex_infusion.toFixed(0),    
    value_text: 'cc/hr',
    message: dex_msg + ' continuous infusion (titrate to maintain glucose 110–250 mg/dL or 6.1-13.9 mmol/L)'
});
