data.logic:
var calc_output = [];
var u = parseFloat(ratio) + parseFloat(chest) + parseFloat(bronchial);
var msg = '';
var mini_msg = '';
if (u == 0) {
    msg = '~0% ARDS risk at 24 hrs post injury';
    mini_msg = 'Mild'
}
if (u == 1 || u == 2 || u == 3 || u == 4) {
    msg = '33% ARDS risk';
    mini_msg = 'Moderate'
}
if (u == 5) {
    msg = '100% ARDS risk';
    mini_msg = 'Severe'
}

calc_output.push({ name: 'mini', value: u, value_text: 'points', message: mini_msg });
calc_output.push({ name: 'BLI Severity Score', value: u, value_text: 'points', message: 'Blast Lung Injury Severity Score' });
calc_output.push({ name: 'ARDS risk', value: mini_msg, value_text: '', message: msg + ' (see Evidence for details)' });
