data.logic:
var calc_output = [];

//user inputs
var woundclass = parseFloat(classification);

var classRN;
var management;
var extra;

if(woundclass === 1){
	classRN = 'Primary';
	management = 'Perform closure within 6-8 hours of trauma. If well-vascularized, may be closed up to 24 hours after trauma.';
    extra = ''
}
if(woundclass === 2){
	classRN = 'Secondary';
	management = 'No surgical closure. Daily dressing changes. Consider negative pressure vacuum therapy.';
    extra = ''
}
if(woundclass === 3){
	classRN = 'Tertiary';
	management = 'Thoroughly explore, irrigate, and debride. Observe for 3-7 days before surgical closure or skin grafting. May benefit from negative pressure vacuum therapy.';
    extra = ' (also known as delayed primary closure)'
}

//results
calc_output.push({
    name: 'mini',
    value: classRN,
    value_text: '',
    message: ''
});
calc_output.push({
    name: 'Wound Closure Classification',
    value: classRN,
    value_text: '',
    message: 'Wound Closure Classification' + extra
});
calc_output.push({
    name: 'Management',
    value: '',
    value_text: '',
    message: management
});
