data.logic:
var calc_output = [];

var msg, minimsg; 

// Declare vars

var hypoglycemia = parseFloat(hypoglycemia);
var emergencydept = parseFloat(emergencydept);
var insulin = parseFloat(insulin);
var sulfonylurea = parseFloat(sulfonylurea);
var kidney = parseFloat(kidney);
var age = parseFloat(age);

if ((hypoglycemia == 2) || (hypoglycemia == 1 && insulin == 1)) {
    msg = 'High'
    minimsg = '>5% risk of hypoglycemia admission in 12 months'
}
else if (hypoglycemia + insulin + sulfonylurea == 0) {
   msg = 'Low'
    minimsg = '<1% risk of hypoglycemia admission in 12 months'
}
else if ((hypoglycemia + insulin + age + kidney == 0) && (sulfonylurea == 1)) {
   msg = 'Low'
    minimsg = '<1% risk of hypoglycemia admission in 12 months'
}
else if ((hypoglycemia + age + emergencydept == 0) && (insulin == 1)) {
   msg = 'Low'
    minimsg = '<1% risk of hypoglycemia admission in 12 months'
}
else {
    msg = 'Intermediate'
    minimsg = '1-5% risk of hypoglycemia admission in 12 months'
}

calc_output.push({
   name: 'mini',
   value: msg,
   value_text: 'risk',
   message: minimsg
});
calc_output.push({
   name: 'result',
   value: msg,
   value_text: 'risk',
   message: minimsg
});
