data.logic:
var calc_output = [];

//user inputs
var deficit = parseFloat(deficit)
var inr = parseFloat(inr)
var gcs = parseFloat(gcs)


var big_score = deficit + (2.5 * inr) + (15 - gcs)

var b = 0.2 * big_score - 5.208

var mort = 1/(1 + Math.exp(-b)) * 100

var msg; 
if (big_score < 16.0) {
    msg = ' Scores ≤16 were associated with lower mortality.'
}
else {
    msg = ' Scores >16 were associated with the highest mortality.'
}

//results
calc_output.push({
    name: 'mini',
    value: big_score.toFixed(2),
    value_text: '',
    message: 'Pediatric BIG Score'
});
calc_output.push({
    name: 'mortality',
    value: mort.toFixed(1),
    value_text: '%',
    message: 'Predicted in-hospital mortality.' + msg
});
calc_output.push({
    name: 'big score',
    value: big_score.toFixed(2),
    value_text: '',
    message: 'Pediatric BIG Score'
});
