data.logic:
var calc_output = [];

//user inputs
if (choose == 0) {
    var weight = parseFloat(weight)
    var dig = parseFloat(dig)
    if (UOMSYSTEM == true) {
        var dig = parseFloat(dig)
    }
    else {
        var dig = parseFloat(dig) / 1.281
    }
    var vials = dig * weight / 100
}

if (choose == 1) {
    var amount = parseFloat(amount)
    var vials = amount / 0.5 * 0.8
}

//results
calc_output.push({
    name: 'mini',
    value: Math.ceil(vials),
    value_text: 'vials',
    message: 'DigiFab dose (40 mg vials)'
});
calc_output.push({
    name: '4036_DigiFab (Digibind) Dosing_result',
    value: Math.ceil(vials),
    value_text: 'vials',
    message: 'DigiFab dose (assumes each vial contains 40 mg)'
});
