data.logic:
var calc_output = [];

var pericardial = parseFloat(pericardial)
var ruq = parseFloat(ruq) 
var luq = parseFloat(luq)
var suprapubic = parseFloat(suprapubic)

var fast = pericardial + ruq + luq + suprapubic

var result, msg, rec_peri, rec_abd

if (fast == 0) { // peri, abd negative
    result = 'Negative FAST'
    msg = 'No pericardial or intra-abdominal fluid seen on FAST'
    rec_peri = ''
    rec_abd = 'Negative FAST does not exclude injury.';
}
if (pericardial == 1 ) { // peri positive
    result = 'Positive FAST'
    msg = 'Suspected hemopericardium';
    rec_peri = 'Surgical intervention warranted for suspected hemopericardium. Median sternotomy preferred if stable; otherwise, use left anterolateral thoracotomy.'; 
    rec_abd = ''
    if (ruq == 1 || luq == 1 || suprapubic == 1 ) { // peri positive and abd positive
        result = 'Positive FAST'
        rec_abd = 'Emergent ex lap indicated for hemoperitoneum in unstable patients.';
        msg = 'Suspected hemopericardium and hemoperitoneum'
    }
    else {
        rec_abd = ''
        result = 'Positive FAST'
    }
} else if (pericardial == 0) { // peri negative
    if (ruq == 1 || luq == 1 || suprapubic == 1) { // abd positive
        result = 'Positive FAST'
        msg = 'Suspected hemoperitoneum';
        rec_peri = ''; 
        rec_abd = 'Emergent ex lap indicated for hemoperitoneum in unstable patients.'
    }
    else if (ruq + luq + suprapubic == 0) { // abd negative
        result = 'Negative FAST'
        msg = 'No pericardial or intra-abdominal fluid seen on FAST'
        rec_peri = ''
        rec_abd = 'Negative FAST does not exclude injury.';
    }
    else { // abd equivocal
        result = 'Equivocal FAST'
        msg = 'Equivocal abdominal view';
        rec_peri = ''; 
        rec_abd = 'Recommend diagnostic peritoneal aspiration (DPA) to rule out hemoperitoneum if unstable. If stable, recommend CT.'
    }
}

else if (pericardial == 2) { // peri equivocal
    if (ruq + luq + suprapubic == 0) { // abd negative
        result = 'Equivocal FAST'
        msg = 'Equivocal pericardial view, negative abdominal view'
        rec_peri = 'Pericardial window or formal transthoracic echocardiography (TTE) recommended to rule out hemopericardium.'
        rec_abd = ''
    }
    else if (ruq == 1 || luq == 1 || suprapubic == 1) { // abd positive
        result = 'Positive FAST'
        msg = 'Suspected hemoperitoneum and equivocal pericardial view'
        rec_abd = 'Emergent ex lap indicated in unstable patients for suspected hemoperitoneum. Pericardial window or formal transthoracic echocardiography (TTE) recommended for equivocal pericardial view.'
        rec_peri = ''
    }
    else if (ruq + luq + suprapubic > 0) {
    result = 'Equivocal FAST' 
    msg = 'Equivocal pericardial view, equivocal abdominal view'
    rec_peri = 'Pericardial window or formal transthoracic echocardiography (TTE) recommended to rule out hemopericardium. ';
    rec_abd = 'Recommend diagnostic peritoneal aspiration (DPA) to rule out hemoperitoneum if unstable. If stable, recommend CT.';
    }
} 

calc_output.push({ 
    name: 'mini', 
    value: result, 
    value_text: '', 
    message: msg 
});
calc_output.push({ 
    name: 'FAST', 
    value: result, 
    value_text: '', 
    message: msg
});
calc_output.push({ 
    name: 'FAST', 
    value: '', 
    value_text: '', 
    message: rec_peri + ' ' + rec_abd + ' Use clinical judgment; see Next Steps for details.'
});
