var calc_output = [];
var mini_msg = '';
var msg = '';
var absentcomplex, rsinterval, dissociation, morphology
if (absentcomplex == 1) {
        mini_msg = 'VT';
        msg = 'ACLS protocol recommends amiodarone and preparing for synchronized cardioversion'
    }
else {
    if (rsinterval == 1) {
        mini_msg = 'VT';
        msg = 'ACLS protocol recommends amiodarone and preparing for synchronized cardioversion'
    } 
    else {
        if (dissociation == 1) {
            mini_msg = 'VT';
            msg = 'ACLS protocol recommends amiodarone and preparing for synchronized cardioversion'
        }
        else {
            if (morphology == 1) {
                mini_msg = 'VT';
                msg = 'ACLS protocol recommends amiodarone and preparing for synchronized cardioversion'
            } 
            else {
                mini_msg = 'SVT';
                msg = 'Refer to ACLS protocol for treatment options (see Next Steps)'
            }
        }
    } 
    }  
calc_output.push({ 
    name: 'mini', 
    value: mini_msg, 
    value_text: '', 
    message: '' 
});
calc_output.push({ 
    name: 'Brugada Criteria', 
    value: mini_msg, 
    value_text: '', 
    message: msg 
});
