data.logic:
var calc_output = [];

var signs = parseFloat(signs)
var indications = parseFloat(indications)
var control = parseFloat(control)


var result, msg, rec

if (signs == 0) {
        result = 'No antivenom';
        msg = 'Apparent dry bite/no bite'
        rec = 'Recommendation: observe patient ≥8 hours, repeat labs prior to discharge, and do not administer antivenom.'
}
else {
    if (indications == 1) {
        result = 'Give antivenom'
        msg = 'Envenomation with indication for antivenom'
        rec = 'Mix 4-6 vials (6-8 vials if shock or serious active bleeding) of crotaline Fab antivenom in 250 mL NS; infuse over 1 hour.'
    }
    else {
        result = 'No antivenom';
        msg = 'Apparent minor envenomation'
        rec = 'Observe patient 12-24 hours, repeat labs at 4-6 hours and prior to discharge, and do not administer antivenom.'
    } 
    // else {
    //         if (control == 1) {
    //             result = 'Give antivenom';
    //             msg = 'Monitor patient. Perform serial examinations, maintenance therapy.'
    //             msg2 = 'Establish IV access, give IV fluids. Mix 4-6 vials of crotaline Fab antivenom in 250 mL NS and infuse over 1 hour (or 6-8 vials if shock or serious active bleeding.'
    //         }
    //         else {
    //             result = 'Repeat antivenom until initial control achieved';
    //             msg = 'Establish IV access, give IV fluids, mix 4-6 vials of crotaline Fab antivenom in 250 ml NS and infuse over 1 hour. Increase dose to 6-8 vials for patients in shock or with serious active bleeding, reexamine after 1 hour.'
    //         }
    //     }
    }
calc_output.push({ 
    name: 'mini', 
    value: result, 
    value_text: '', 
    message: '' 
});
calc_output.push({ 
    name: 'Antivenom Dosing', 
    value: result, 
    value_text: '', 
    message: msg 
});
calc_output.push({ 
    name: 'Recommendation', 
    value: ' ', 
    value_text: '', 
    message: rec 
});

if (result == 'Give antivenom') {
calc_output.push({ 
    name: 'Next steps', 
    value: ' ', 
    value_text: '', 
    message: 'Re-examine after 1 hour. If envenomation controlled, monitor patient, perform serial examinations, and give maintenance therapy (2 vials of antivenom every 6 hours x 3 doses). See Evidence for details.'
});
}
