data.logic:
var calc_output = [];

var age = parseFloat(age)
var sex = parseInt(sex)
var ht = parseFloat(ht)
var sbp = parseInt(sbp)
var dbp = parseInt(dbp)

if (UOMSYSTEM == true) {
    ht = ht * 2.54
}

// height constants
if (sex == 0) {
    var b0 = [71.0728, 102.6662, 13.6814, 10.9219] // sbp 90, sbp 95, dbp 90, dbp 95 for boys
} else {
    var b0 = [92.4497, 117.6411, 31.5473, 42.9427] // sbp 90, sbp 95, dbp 90, dbp 95 for girls
}

function add(a, b) {
    return a + b;
}

// knots
if (sex == 0) { // boys
    var knot_age = [5.06, 10.79, 13.22, 14.51, 17.30]
    var knot_ht = [107.8, 140.0, 154.5, 166.4, 179.1]
    var knot_ageht = [-15.000, 8.900, 50.375, 112.684, 250.040]
} else { // girls
    var knot_age = [5.00, 10.70, 13.16, 14.51, 17.33]
    var knot_ht = [106.7, 140.7, 154.0, 160.5, 168.9]
    var knot_ageht = [6.701, 16.438, 46.800, 84.460, 203.608]
}

if (sex == 0) {
    height_c = 150
} else {
    height_c = 147
}
var ageht = (age - 10) * (ht - height_c)

// regression equations
var y2a = Math.max(age - knot_age[0], 0)
var y2b = Math.max(age - knot_age[3], 0)
var y2c = Math.max(age - knot_age[4], 0)
var y3a = Math.max(age - knot_age[1], 0)
var y4a = Math.max(age - knot_age[2], 0)

var x2a = Math.max(ht - knot_ht[0], 0)
var x2b = Math.max(ht - knot_ht[3], 0)
var x2c = Math.max(ht - knot_ht[4], 0)
var x3a = Math.max(ht - knot_ht[1], 0)
var x4a = Math.max(ht - knot_ht[2], 0)

var w2a = Math.max(ageht - knot_ageht[0], 0)
var w2b = Math.max(ageht - knot_ageht[3], 0)
var w2c = Math.max(ageht - knot_ageht[4], 0)
var w3a = Math.max(ageht - knot_ageht[1], 0)
var w4a = Math.max(ageht - knot_ageht[2], 0)

// age parameters: variables
var age1 = Math.pow(y2a, 3) - Math.pow(y2b, 3) * (knot_age[4] - knot_age[0]) / (knot_age[4] - knot_age[3]) + Math.pow(y2c, 3) * (knot_age[3] - knot_age[0]) / (knot_age[4] - knot_age[3])
var age2 = Math.pow(y3a, 3) - Math.pow(y2b, 3) * (knot_age[4] - knot_age[1]) / (knot_age[4] - knot_age[3]) + Math.pow(y2c, 3) * (knot_age[3] - knot_age[1]) / (knot_age[4] - knot_age[3])
var age3 = Math.pow(y4a, 3) - Math.pow(y2b, 3) * (knot_age[4] - knot_age[2]) / (knot_age[4] - knot_age[3]) + Math.pow(y2c, 3) * (knot_age[3] - knot_age[2]) / (knot_age[4] - knot_age[3])

var age_var = [age, age1 / 100, age2 / 100, age3 / 100]

// age parameters: constants
if (sex == 0) { // boys
    var age_con_90 = [-0.5897, 0.9540, -3.2255, 5.8280]
    var age_con_95 = [-1.2263, 0.8589, 3.2534, -15.7885]
    var age_con_90d = [1.7899, -2.0949, 11.8906, -20.3017]
    var age_con_95d = [1.7121, -1.5447, 6.0756, -5.5880]
} else { // girls
    var age_con_90 = [-1.8508, 2.5523, -13.6421, 26.2260]
    var age_con_95 = [-2.0964, 2.6981, -11.2572, 13.4182]
    var age_con_90d = [-0.1910, 0.8482, -4.9950, 5.5448]
    var age_con_95d = [-0.2087, 1.2659, -7.7504, 8.4270]
}

// height parameters: variables
var ht1 = Math.pow(x2a, 3) - Math.pow(x2b, 3) * (knot_ht[4] - knot_ht[0]) / (knot_ht[4] - knot_ht[3]) + Math.pow(x2c, 3) * (knot_ht[3] - knot_ht[0]) / (knot_ht[4] - knot_ht[3])
var ht2 = Math.pow(x3a, 3) - Math.pow(x2b, 3) * (knot_ht[4] - knot_ht[1]) / (knot_ht[4] - knot_ht[3]) + Math.pow(x2c, 3) * (knot_ht[3] - knot_ht[1]) / (knot_ht[4] - knot_ht[3])
var ht3 = Math.pow(x4a, 3) - Math.pow(x2b, 3) * (knot_ht[4] - knot_ht[2]) / (knot_ht[4] - knot_ht[3]) + Math.pow(x2c, 3) * (knot_ht[3] - knot_ht[2]) / (knot_ht[4] - knot_ht[3])

var ht_var = [ht, ht1 / 100, ht2 / 100, ht3 / 100]

// height parameters: constants
if (sex == 0) { // boys
    var ht_con_90 = [0.3275, -0.0005, 0.0181, -0.0707]
    var ht_con_95 = [0.1576, 0.0072, -0.0076, -0.0516]
    var ht_con_90d = [0.3408, -0.0062, 0.0239, -0.0268]
    var ht_con_95d = [0.3914, -0.0095, 0.0458, -0.0634]
} else { // girls
    var ht_con_90 = [0.2495, 0.0000, 0.0494, -0.3050]
    var ht_con_95 = [0.0991, 0.0053, 0.0132, -0.1836]
    var ht_con_90d = [0.3330, -0.0093, 0.0772, -0.2338]
    var ht_con_95d = [0.2634, -0.0082, 0.0706, -0.1836]
}

// age-height parameters: variables
var ageht1 = Math.pow(w2a, 3) - Math.pow(w2b, 3) * (knot_ageht[4] - knot_ageht[0]) / (knot_ageht[4] - knot_ageht[3]) + Math.pow(w2c, 3) * (knot_ageht[3] - knot_ageht[0]) / (knot_ageht[4] - knot_ageht[3])
var ageht2 = Math.pow(w3a, 3) - Math.pow(w2b, 3) * (knot_ageht[4] - knot_ageht[1]) / (knot_ageht[4] - knot_ageht[3]) + Math.pow(w2c, 3) * (knot_ageht[3] - knot_ageht[1]) / (knot_ageht[4] - knot_ageht[3])
var ageht3 = Math.pow(w4a, 3) - Math.pow(w2b, 3) * (knot_ageht[4] - knot_ageht[2]) / (knot_ageht[4] - knot_ageht[3]) + Math.pow(w2c, 3) * (knot_ageht[3] - knot_ageht[2]) / (knot_ageht[4] - knot_ageht[3])

var ageht_var = [ageht, ageht1 / 10000, ageht2 / 10000, ageht3 / 10000]

// age-height parameters: constants
if (sex == 0) { // boys
    var ageht_con_90 = [-0.0200, 0.1457, -0.2721, 0.1573]
    var ageht_con_95 = [-0.0325, 0.0706, -0.1432, 0.1009]
    var ageht_con_90d = [-0.0499, 0.1898, -0.2958, 0.0953]
    var ageht_con_95d = [-0.0340, 0.1515, -0.2269, 0.0567]
} else { // girls
    var ageht_con_90 = [-0.0184, -0.1813, 0.2673, -0.1041]
    var ageht_con_95 = [-0.0338, -0.4799, 0.7190, -0.3037]
    var ageht_con_90d = [-0.0012, -0.3665, 0.5893, -0.3214]
    var ageht_con_95d = [0.0138, -0.4419, 0.6751, -0.3217]
}

// combine age, height, and age-height parameters 
var age_sum_90 = 0;
var ht_sum_90 = 0;
var ageht_sum_90 = 0;
for (var i = 0; i < age_var.length; i++) {
    age_sum_90 += age_var[i] * age_con_90[i];
    ht_sum_90 += ht_var[i] * ht_con_90[i];
    ageht_sum_90 += ageht_var[i] * ageht_con_90[i];
}

var age_sum_95 = 0;
var ht_sum_95 = 0;
var ageht_sum_95 = 0;
for (var i = 0; i < age_var.length; i++) {
    age_sum_95 += age_var[i] * age_con_95[i];
    ht_sum_95 += ht_var[i] * ht_con_95[i];
    ageht_sum_95 += ageht_var[i] * ageht_con_95[i];
}

var age_sum_90d = 0;
var ht_sum_90d = 0;
var ageht_sum_90d = 0;
for (var i = 0; i < age_var.length; i++) {
    age_sum_90d += age_var[i] * age_con_90d[i];
    ht_sum_90d += ht_var[i] * ht_con_90d[i];
    ageht_sum_90d += ageht_var[i] * ageht_con_90d[i];
}

var age_sum_95d = 0;
var ht_sum_95d = 0;
var ageht_sum_95d = 0;
for (var i = 0; i < age_var.length; i++) {
    age_sum_95d += age_var[i] * age_con_95d[i];
    ht_sum_95d += ht_var[i] * ht_con_95d[i];
    ageht_sum_95d += ageht_var[i] * ageht_con_95d[i];
}

// 90th percentile SBP
var sbp_90 = b0[0] + age_sum_90 + ht_sum_90 + ageht_sum_90

// 95th percentile SBP
var sbp_95 = b0[1] + age_sum_95 + ht_sum_95 + ageht_sum_95

// 90th percentile DBP
var dbp_90 = b0[2] + age_sum_90d + ht_sum_90d + ageht_sum_90d

// 95th percentile DBP
var dbp_95 = b0[3] + age_sum_95d + ht_sum_95d + ageht_sum_95d

// interpretation

var sbp_94 = sbp_95 - 1
var dbp_94 = dbp_95 - 1
var sbp_96 = sbp_95 + 12
var dbp_96 = dbp_95 + 12
var sbp_99 = sbp_95 + 30
var dbp_99 = dbp_95 + 30

sbp_90 = sbp_90.toFixed(0)
sbp_94 = sbp_94.toFixed(0)
sbp_95 = sbp_95.toFixed(0)
sbp_96 = sbp_96.toFixed(0)
sbp_99 = sbp_99.toFixed(0)

dbp_90 = dbp_90.toFixed(0)
dbp_94 = dbp_94.toFixed(0)
dbp_95 = dbp_95.toFixed(0)
dbp_96 = dbp_96.toFixed(0)
dbp_99 = dbp_99.toFixed(0)

if (sex == 0) {
    sex = 'boy'
} else if (sex == 1) {
    sex = 'girl'
}

var result, msg, mgmt

if (age < 13) {
    if (sbp_90 > 120) {
        var sbp_90 = 120
    }
    if (dbp_90 > 80) {
        var dbp_90 = 80
    }
    if (sbp_95 > 130) {
        var sbp_95 = 130
    }
    if (sbp_96 > 140) {
        var sbp_96 = 140
    }
    if (dbp_96 > 90) {
        var dbp_96 = 90
    }

    var sbp_index

    if (sbp < sbp_90) {
        sbp_index = 0
    } else if (sbp >= sbp_90 && sbp < sbp_95) {
        sbp_index = 1
    } else if (sbp >= sbp_95 && sbp < sbp_96) {
        sbp_index = 2
    } else if (sbp >= sbp_96 && sbp < sbp_99) {
        sbp_index = 3
    } else if (sbp >= sbp_99) {
        sbp_index = 4
    }

    var dbp_index

    if (dbp < dbp_90) {
        dbp_index = 0
    } else if (dbp >= dbp_90 && dbp < dbp_95) {
        dbp_index = 1
    } else if (dbp >= dbp_95 && dbp < dbp_96) {
        dbp_index = 2
    } else if (dbp >= dbp_96 && dbp < dbp_99) {
        dbp_index = 3
    } else if (dbp >= dbp_99) {
        dbp_index = 4
    }

    var normal = 'Normal BP'
    var elevated = 'Elevated BP'
    var stage1 = 'Stage 1 HTN'
    var stage2 = 'Stage 2 HTN'
    var critical = 'Critical HTN'

    var results = [
        [normal, elevated, stage1, stage2, critical],
        [elevated, elevated, stage1, stage2, critical],
        [stage1, stage1, stage1, stage2, critical],
        [stage2, stage2, stage2, stage2, critical],
        [critical, critical, critical, critical, critical]
    ]

    var result = results[sbp_index][dbp_index]

    var msg, mgmt

    if (result == normal) {
        msg = "Normal BP in this " + age + "-year-old " + sex + " is SBP <" + sbp_90 + " and DBP <" + dbp_90 + " (see Evidence for details)"
        mgmt = 'Check BP at next routine well-child visit.'
    } else if (result == elevated) {
        msg = "Elevated BP in this " + age + "-year-old " + sex + " is SBP " + sbp_90 + "-" + sbp_94 + " and/or DBP " + dbp_90 + "-" + dbp_94 + " (see Evidence for details)"
        mgmt = 'Initiate lifestyle interventions; re-check BP in 6 months. See Next Steps for details.'
    } else if (result == stage1) {
        msg = "Stage 1 HTN in this " + age + "-year-old " + sex + " is SBP " + sbp_95 + "-" + sbp_96 + " and/or DBP " + dbp_95 + "-" + dbp_96 + " (see Evidence for details)"
        mgmt = 'If asymptomatic, initiate lifestyle interventions and recheck BP in 1-2 weeks. See Next Steps for details.'
    } else if (result == stage2) {
        msg = "Stage 2 HTN in this " + age + "-year-old " + sex + " is SBP ≥" + sbp_96 + " and/or DBP ≥" + dbp_96 + " (see Evidence for details)"
        mgmt = 'If asymptomatic, initiate lifestyle interventions and recheck BP in 1 week, OR refer directly to subspecialist within 1 week. See Next Steps for details.'
    } else if (result == critical) {
        msg = "Critical HTN in this " + age + "-year-old " + sex + " is SBP ≥" + sbp_99 + " and/or DBP ≥" + dbp_99 + " (see Evidence for details)"
        mgmt = 'Consider urgent evaluation and treatment.'
    }
}


    if (age >= 13) { // different limits if patient is 13 years or older
        if (sbp < 120 && dbp < 80) {
            result = 'Normal BP'
            msg = "Normal BP in this " + age + "-year-old " + sex + " is SBP <120 and DBP <80"
        }  if (sbp >= 120 && sbp <= 129 && dbp < 80) {
            result = 'Elevated BP'
            msg = "Elevated BP in this " + age + "-year-old " + sex + " is SBP 120-129 and DBP <80"
        }  if ((sbp >= 130 && sbp <= 139) || (dbp >= 80 && dbp <= 89)) {
            result = 'Stage 1 HTN'
            msg = "Stage 1 HTN in this " + age + "-year-old " + sex + " is SBP 130-139 and/or DBP 80-89"
        }  if (sbp >= 140 || dbp >= 90) {
            result = 'Stage 2 HTN'
            msg = "Stage 2 HTN in this " + age + "-year-old " + sex + " is SBP ≥140 and/or DBP ≥90"
        }
    }

    calc_output.push({
        name: 'mini',
        value: result,
        value_text: '',
        message: msg
    });
    calc_output.push({
        name: 'pt sbp',
        value: result,
        value_text: '',
        message: msg
    });
    calc_output.push({
        name: 'rec',
        value: '',
        value_text: '',
        message: mgmt
    });
