data.logic:
var calc_output = [];

var risk; 
var survival;
var age = parseFloat(age);
var ecog = parseFloat(ecog);
var ldh = parseFloat(ldh);
var ulnldh = parseFloat(ulnldh);
var wbc = parseFloat(wbc);
var index = parseFloat(index);
var scorename = 'MIPI'

if (index == null) {
  index = 0
}
if (index > 0) {
  scorename = 'MIPIb'
}

var score = 0.03535 * age + ecog * 0.6978 + 1.367 * Math.log10(ldh/ulnldh) + 0.9393 * Math.log10(wbc*1000) + 0.02142 * index

if (score.toFixed(1) < 5.7) {
  risk = 'Low';
  survival = '5 year survival of 60%';
  if (index > 0) {
    survival = 'Median overall survival not reached'
  }
}
if (score.toFixed(1) >= 5.7 && score.toFixed(1) < 6.2) {
  risk = 'Intermediate';
  survival = 'Median overall survival 51 months';
  if (index > 0) {
    survival = 'Median overall survival 58 months'
  }
}
if (score.toFixed(1) >= 6.2) {
  risk = 'High';
  survival = 'Median overall survival 29 months';
  if (index > 0 && score.toFixed(1) >= 6.5) {
    survival = 'Median overall survival 37 months';
    risk = 'High';
  } else if (index > 0 && score.toFixed(1) < 6.5) {
    survival = 'Median overall survival 58 months';
    risk = 'Intermediate';
  }
}

calc_output.push({
  name: 'mini',
  value: score.toFixed(1),
  value_text: ' points',
  message: risk + ' risk'
});
calc_output.push({
  name: 'MIPI score',
  value: score.toFixed(1),
  value_text: ' points',
  message: scorename
});
calc_output.push({
  name: 'Risk message',
  value: risk,
  value_text: ' risk',
  message: survival
});
