data.logic:
var calc_output = [];

// Declare vars

var grade; 
var findings; 
var description; 
var survival;
findings = parseFloat(findings);

if (findings == 0) {
    grade = 'Grade 0';
    description = 'No injury';
    survival = '84% in-hospital survival';
}
if (findings == 1) {
   grade = 'Grade 1';
   description = 'Mild injury';
   survival = '84% in-hospital survival';
}
if (findings == 2){
   grade = 'Grade 2';
   description = 'Moderate injury';
   survival = '57% in-hospital survival';
}
if (findings == 3) {
   grade = 'Grade 3';
   description = 'Severe injury';
   survival = '57% in-hospital survival';
}
if (findings == 4) {
   grade = 'Grade 4';
   description = 'Massive injury';
   survival = '57% in-hospital survival';
}

calc_output.push({
   name: 'mini',
   value: grade,
   value_text: '',
   message: description
});
calc_output.push({
   name: 'grade',
   value: grade,
   value_text: '',
   message: 'AIS for inhalation injury'
});
calc_output.push({
   name: 'severity',
   value: description,
   value_text: '',
   message: survival
});
