data.logic:
var calc_output = [];

// Declare vars

var grade;
var findings;
var description; 
findings = parseFloat(findings);

if (findings == 0) {
    grade = 'Grade A',
    description = 'Mild'
}
if (findings == 1) {
   grade = 'Grade B',
   description = 'Mild'
}
if (findings == 2){
   grade = 'Grade C',
   description = 'Severe'
}
if (findings == 3) {
   grade = 'Grade D',
   description = 'Severe'
}

calc_output.push({
   name: 'mini',
   value: grade,
   value_text: '',
   message: description
});
calc_output.push({
   name: 'result',
   value: grade,
   value_text: '',
   message: 'LA Grading of Esophagitis'
});
calc_output.push({
   name: 'result',
   value: description,
   value_text: 'reflux esophagitis',
   message: ''
});
