data.logic:
var calc_output = [];

var fi = parseFloat(fi)/100;
var pa = parseFloat(pa);

if (UOMSYSTEM == false) {
  pa = pa*7.5187969925;
}

var ratio = pa/fi;

ratio = ratio.toFixed(1);
var risk;
var mort;

if (ratio > 300) {
  risk = 'Not ARDS';
  mort = '';
} else if (ratio > 200 && ratio <= 300) {
  risk = 'Mild';
  mort = '27';
} else if (ratio > 100 && ratio <= 200) {
  risk = 'Moderate';
  mort = '32';
} else if (ratio <= 100) {
  risk = 'Severe';
  mort = '45';
}


//results
if (UOMSYSTEM == true) {
  calc_output.push({
      name: 'mini',
      value: ratio,
      value_text: 'mmHg',
      message: 'Horowitz Index (P/F ratio)'
  });
  calc_output.push({
      name: '4062_Horowitz Index_result (score)',
      value: ratio,
      value_text: 'mmHg',
      message: 'Horowitz Index (P/F ratio)'
  });
}
if (UOMSYSTEM == false) {
  calc_output.push({
      name: 'mini',
      value: (ratio/7.5187969925).toFixed(1),
      value_text: 'kPA',
      message: 'Horowitz Index (P/F ratio)'
  });
  calc_output.push({
      name: '4062_Horowitz Index_result (score)',
      value: (ratio/7.5187969925).toFixed(1),
      value_text: 'kPA',
      message: 'Horowitz Index (P/F ratio)'
  });
}
calc_output.push({
    name: '4062_Horowitz Index_interpretation',
    value: risk,
    value_text: '',
    message: 'Severity of ARDS'
});
if (ratio <= 300) {
calc_output.push({
    name: '4062_Horowitz Index_result (mortality)',
    value: mort,
    value_text: '%',
    message: 'Mortality'
});
}
