data.logic:
var calc_output = [];

// Declare vars

var alt = parseFloat(alt);
var alt_uln = parseFloat(alt_uln);
var alp = parseFloat(alp);
var alp_uln = parseFloat(alp_uln);

var r = alt / alt_uln / ( alp / alp_uln );
var interpretation;
var rec;

if (r.toFixed(1) < 2) {
	interpretation = 'Cholestatic';
	rec = 'Recommend imaging studies (e.g. abdominal ultrasound)';
}
else if (r.toFixed(1) >= 2 && r.toFixed(1) <= 5) {
	interpretation = 'Mixed';
	rec = 'Recommend acute viral hepatitis serologies, HCV RNA and autoimmune hepatitis serologies, imaging studies (e.g. abdominal ultrasound)';
}
else if (r.toFixed(1) > 5) {
	interpretation = 'Hepatocellular';
	rec = 'Recommend acute viral hepatitis serologies, HCV RNA and autoimmune hepatitis serologies, imaging studies (e.g. abdominal ultrasound)';
}

calc_output.push({
   name: 'mini',
   value: r.toFixed(1),
   value_text: '',
   message: interpretation
});
calc_output.push({
   name: '4064_R Factor_result',
   value: r.toFixed(1),
   value_text: '',
   message: 'R Factor'
});
calc_output.push({
   name: '4064_R Factor_interpretation',
   value: interpretation,
   value_text: 'injury',
   message: rec
});
