data.logic:
var calc_output = [];

var height = parseFloat(height);
var weight = parseFloat(weight);
var weight_baby = parseFloat(weight_baby);
var weight_peds = parseFloat(weight_peds);
var hematocrit = parseFloat(hematocrit) || 0;
var sex = parseFloat(sex);
var patient = parseFloat(patient);

var tbv;

var factor = [ "100", "85", "75", "70" ];

if (patient <= 1) { /* preterm or term neonate */
	UOMSYSTEM ? (weight = weight_baby * 28.3495) : (weight = weight_baby)
 	tbv = weight / 1000 * factor[patient]
}
else if (patient == 2) { /* infant */
	UOMSYSTEM ? (weight = weight_infant * 0.453592) : (weight = weight_infant)
	tbv = weight * factor[patient]
}
else if (patient == 3) { /* small child */
	UOMSYSTEM ? (weight = weight_peds * 0.453592) : (weight = weight_peds)
	tbv = weight * factor[patient]
}
else if (patient >= 4) { /* adult */
	UOMSYSTEM ? (weight = weight / 2.20462) : (weight = weight) 
	if (sex	== 0) { // male
		tbv = 0.3669 * Math.pow(height / 100, 3) + 0.03219 * weight + 0.6041
	}
	else {
		tbv = 0.3561 * Math.pow(height / 100, 3) + 0.03308 * weight + 0.1833
	}
	tbv = tbv * 1000
}

if (hematocrit > 0) {
  var rbc = tbv * hematocrit / 100;
  var pv = tbv * (1 - hematocrit / 100);
}

if (patient == 4) {
      tbv = tbv / 1000
  }

//results
if (patient == 4) {
calc_output.push({
    name: 'mini',
    value: tbv.toFixed(1),
    value_text: 'L',
    message: 'Total blood volume'
});
calc_output.push({
    name: '4065_Blood Volume Calculation_result (TBV)',
    value: tbv.toFixed(1),
    value_text: 'L',
    message: 'Total blood volume'
});
}
else {
calc_output.push({
    name: 'mini',
    value: tbv.toFixed(0).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ","),
    value_text: 'mL',
    message: 'Total blood volume'
});
calc_output.push({
    name: '4065_Blood Volume Calculation_result (TBV)',
    value: tbv.toFixed(0).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ","),
    value_text: 'mL',
    message: 'Total blood volume'
});
}
if (hematocrit > 0) {
  calc_output.push({
      name: '4065_Blood Volume Calculation_result (RBC)',
      value: rbc.toFixed(0).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ","),
      value_text: 'mL',
      message: 'RBC volume'
  });
  calc_output.push({
      name: '4065_Blood Volume Calculation_result (PV)',
      value: pv.toFixed(0).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ","),
      value_text: 'mL',
      message: 'Plasma volume'
  });
}
