data.logic:
var calc_output = [];

var sex = parseFloat(sex);
var age = parseFloat(age);
var weight = parseFloat(weight); // no need to adjust, weight always comes in as SI
var height = parseFloat(height)/2.54; // height in inches

if (UOMSYSTEM == true) { // US units
    var cr = parseFloat(cr);
} else if (UOMSYSTEM == false) {
    var cr = parseFloat(cr)/88.4;
}

if (sex == 0) {
    var mult = 1;
} else if (sex == 1) {
    var mult = 0.85;
}

var crcl = (140 - age)*weight*mult / (72*cr);

calc_output.push({
    name: 'mini',
    value: crcl.toFixed(0),
    value_text: 'mL/min',
    message: 'Creatinine clearance'
});

if (!parseFloat(height)) {
    calc_output.push({
        name: '43_Creatinine Clearance_result (original)',
        value: crcl.toFixed(0),
        value_text: 'mL/min',
        message: 'Creatinine clearance, original Cockcroft-Gault'
    });
}
else if (parseFloat(height)) {
    if (sex == 0) {
        var ibw = 50 + (2.3*(height - 60));
    } else if (sex == 1) {
        var ibw = 45.5 + (2.3*(height - 60));
    }
    var ibw_lbs = ibw*2.205;

    var crcl_ibw = (140 - age)*ibw*mult / (72*cr);
    var height_m = height*2.54 / 100;
    var bmi = weight/Math.pow(height_m, 2);

    var abw = ibw + 0.4*(weight - ibw);
    var abw_lbs = abw*2.205;
    var crcl_abw = (140 - age)*abw*mult / (72*cr);

    if (bmi < 18.5) {
        calc_output.push({
            name: '43_Creatinine Clearance_result (original)',
            value: crcl.toFixed(0),
            value_text: 'mL/min',
            message: 'Creatinine clearance for underweight patient (BMI ' + bmi.toFixed(1) + ' kg/m²), calculated using actual body weight (no adjustment).'
        })

    } else if (bmi >= 18.5 && bmi < 25) {
        calc_output.push({
            name: '43_Creatinine Clearance_result (original)',
            value: crcl.toFixed(0),
            value_text: 'mL/min',
            message: 'Creatinine clearance, original Cockcroft-Gault'
        });

        var msg = '';
        if (weight.toFixed(0) == ibw.toFixed(0)) {
            msg = 'normal weight patient. Note: This patient\'s IBW is equal to their actual weight, so this is likely near their true CrCl and no adjustment is made.';
        } else {
            msg = 'normal weight patient, using ideal body weight of ' + ibw.toFixed(0) + ' kg (' + (ibw_lbs).toFixed(0) + ' lbs).';
        }

        calc_output.push({
            name: '43_Creatinine Clearance_result (modified)',
            value: crcl_ibw.toFixed(0),
            value_text: 'mL/min',
            message: 'Creatinine clearance for ' + msg
        });

        if (weight !== ibw) {
            var range;
            if (weight > ibw) {
                    range = crcl_ibw.toFixed(1) + '-' + crcl.toFixed(1);
            } else {
                    range = crcl.toFixed(1) + '-' + crcl_ibw.toFixed(1);
            }
            calc_output.push({
                name: '43_Creatinine Clearance_result (range)',
                value: range,
                value_text: 'mL/min',
                message: "Note: This range uses IBW and actual body weight. Controversy exists over which form of weight to use."
            });
        }
    } else if (bmi >= 25) {
        calc_output.push({
            name: '43_Creatinine Clearance_result (original)',
            value: crcl.toFixed(0),
            value_text: 'mL/min',
            message: 'Creatinine clearance, original Cockcroft-Gault'
        });

        calc_output.push({
            name: '43_Creatinine Clearance_result (modified)',
            value: crcl_abw.toFixed(0),
            value_text: 'mL/min',
            message: 'Creatinine clearance modified for overweight patient, using adjusted body weight of ' + abw.toFixed(0) + ' kg (' + abw_lbs.toFixed(0) + ' lbs).'
        });

        if (crcl_ibw !== crcl_abw) {
            var range;
            if (crcl_ibw < crcl_abw) {
                range = crcl_ibw.toFixed(1) + '-' + crcl_abw.toFixed(1);
            } else {
                range = crcl_abw.toFixed(1) + '-' + crcl_ibw.toFixed(1);
            }
        }

        calc_output.push({
            name: '43_Creatinine Clearance_result (range)',
            value: range,
            value_text: 'mL/min',
            message: "Note: This range uses IBW and adjusted body weight. Controversy exists over which form of weight to use."
        });
    }
}
