data.logic:
var calc_output = [];
var heart_rate = parseFloat(heart_rate);
var rr = 60/heart_rate;
var qt_interval = parseFloat(qt_interval);
var qt_interval2 = parseFloat(qt_interval2);
var formula = parseFloat(formula);
var speed = parseFloat(speed);

if (speed == 25) {
  qt_interval = qt_interval;
  if (UOMSYSTEM == true) { 
  	qt_interval = qt_interval*40;
  }
} else {
  qt_interval = qt_interval2;
  if (UOMSYSTEM == true) { 
  	qt_interval = qt_interval*20;
  }
}


var bazett = qt_interval/Math.sqrt(rr);
var fridericia = qt_interval/Math.pow(rr,(1/3));
var framingham = qt_interval + 154*(1 - rr);
var hodges = qt_interval + 1.75*(60 / rr - 60);
var rautaharju = qt_interval * (120 + heart_rate)/180;

if (formula == 0) {
  v = bazett;
} else if (formula == 1) {
  v = fridericia;
} else if (formula == 2) {
  v = framingham;
} else if (formula == 3) {
  v = hodges;
} else if (formula == 4) {
  v = rautaharju;
}

calc_output.push({
  name:'mini',
  value:v.toFixed(0),
  value_text:'msec',
  message:'Corrected QT Interval'
}); 
calc_output.push({
  name:'48_Corrected QT_result',
  value:v.toFixed(0),
  value_text:'msec',
  message:'Corrected QT Interval (QTc)'
});
