data.logic:
var calc_output = [];
var sodium = parseFloat(sodium);
var glucose;

if (UOMSYSTEM == true) {
  glucose = parseFloat(glucose);
}

if (UOMSYSTEM == false) // need US units for glucose
{
  glucose = parseFloat(glucose) / 0.0555;
}

var value = sodium + (0.016 * (glucose - 100)); // value in US units
var value2 = sodium + (0.024 * (glucose - 100)); // value2 in US units

var units = 'mEq/L'; // value in US units

if (UOMSYSTEM == false) { 
  units = 'mmol/L' // value in SI units
}

  calc_output.push({
    name: 'mini',
    value: value.toFixed(0),
    value_text: units,
    message: 'Corrected Sodium'
  });
  calc_output.push({
    name: '50_Na Correction for Hyperglycemia_result (Katz)',
    value: value.toFixed(0),
    value_text: units,
    message: 'Corrected Sodium<br/>(Katz, 1973)'
  });
  calc_output.push({
    name: '50_Na Correction for Hyperglycemia_result (Hillier)',
    value: value2.toFixed(0),
    value_text: units,
    message: 'Corrected Sodium<br/>(Hillier, 1999)'
  });
