data.logic:
var calc_output = [];
var u = parseFloat(ph) + parseFloat(inr) + parseFloat(creatinine) + parseFloat(hepatic_encphalopathy);
var lactate = parseFloat(lactate);
var phosphorus = parseFloat(phosphorus);
var msg = '';
var msgadd = '';
var mini_msg = '';
if (u == 3) {
    msg = 'Transfer to transplant center and list for transplant if INR, Creatinine, and Encephalopathy indicators all present in a 24 hour period.';
    mini_msg = 'Transfer for transplant if all present in 24 hr period.'
}
if (u > 3) {
    msg = 'Transfer to transplant center and list for transplant.';
    mini_msg = 'Transfer for transplant.'
}
if (u < 3) {
    msg = 'Does not meet transplant or referral criteria.';
    mini_msg = 'Does not meet transplant or referral criteria.'
}
if (parseFloat(ph) == 1) {
    msg = 'Transfer to transplant center and list for transplant.';
    mini_msg = 'Transfer for transplant.'
}
if (lactate == 1 && phosphorus == 0) { msgadd = '<br/><br/>While not part of the King\u2019s Criteria, hyperlactatemia is a strong predictor of poor prognosis for survival without transplantation.'; }
if (lactate == 0 && phosphorus == 1) { msgadd = '<br/><br/>While not part of the King\u2019s Criteria, hyperphosphatemia is a strong predictor of poor prognosis for survival without transplantation.'; }
if (lactate == 1 && phosphorus == 1) { msgadd = '<br/><br/>While not part of the King\u2019s Criteria, hyperlactatemia and hyperphosphatemia are strong predictors of poor prognosis for survival without transplantation.'; }

calc_output.push({ 
    name: 'mini', 
    value: u, 
    value_text: 'criteria', 
    message: mini_msg 
});
calc_output.push({ 
    name: "532_King's College Criteria_result", 
    value: u, 
    value_text: 'criteria', 
    message: msg + msgadd 
});
