data.logic:
var calc_output = [];

var u = parseFloat(skull) + parseFloat(scalp) + parseFloat(deficit) + parseFloat(alert) + parseFloat(behavior) + parseFloat(coag) + parseFloat(vomit);

var rec, mini_msg, risk

if (u == 0) {
    rec = 'CT not necessary (100% sensitive for findings requiring neurosurgical intervention)';
    mini_msg = 'CT not necessary';
    risk = 'Low'
}
else if (u > 0) {
    rec = 'Consider CT';
    mini_msg = 'Consider CT';
    risk = 'High'
}
calc_output.push({ 
	name: 'mini',
	value: risk,
	value_text: 'risk',
	message: mini_msg
});
calc_output.push({ 
	name: 'NEXUS CT Head Rule',
	value: risk,
	value_text: 'risk',
	message: rec
});
