data.logic:
var calc_output = [];
var u, load, maint, msg, maint4hr, maint16hr, maint4hrvol, maint16hrvol;
u = parseFloat(weight);
if (nac_route == 1) {
    load = Math.round(140 * u);
    maint = Math.round(70 * u);
    msg = 'PO Dosing: 72-Hour Regimen, 18 Doses Total<br/>Total of 1,330 mg/kg Delivered:<br/><br/>Loading Dose of: ' + load + ' mg PO, then<br/>Maintenance Dose: ' + maint + ' mg PO every 4 hours for a total of 72 hours.<br/><br/>Note: If the patient vomits within 1 hour of administration, dose must be repeated.'
}
if (nac_route == 0) {
    if (u >= 100) { msg = 'The patient\u2018s weight is outside the FDA\u2018s dosing guidelines (>100kg). The manufacturer of IV Mucomyst recommends checking with a toxicologist for these patients.' }
    if (u >= 40) {
        load = Math.round(150 * u);
        maint4hr = Math.round(50 * u);
        maint16hr = Math.round(100 * u);
        msg = 'IV Dosing: 21-Hour Regimen, \u20183 Bag Dosing\u2018<br/><br/>Loading Dose of: ' + load + ' mg IV in 200 mL of diluent over 60 min, then<br/>' + maint4hr + ' mg IV in 500 mL of diluent over 4 hr then<br/>' + maint16hr + ' mg IV in 1000 mL of diluent over 16 hr.'
    }
    if ((u > 20) && (u < 40)) {
        load = Math.round(150 * u);
        maint4hr = Math.round(50 * u);
        maint16hr = Math.round(100 * u);
        msg = 'IV Dosing: 21-Hour Regimen, \u20183 Bag Dosing\u2018<br/><br/>Loading Dose of: ' + load + ' mg IV in 100 mL of diluent over 60 min, then<br/>' + maint4hr + ' mg IV in 250 mL of diluent over 4 hr then<br/>' + maint16hr + ' mg IV in 500 mL of diluent over 16 hr.'
    }
    if (u <= 20) {
        load = Math.round(150 * u);
        loadvol = Math.round(3 * u);
        maint4hr = Math.round(50 * u);
        maint4hrvol = Math.round(7 * u);
        maint16hr = Math.round(100 * u);
        maint16hrvol = Math.round(14 * u);
        msg = 'IV Dosing: 21-Hour Regimen, \u201c3 Bag Dosing\u201c<br/><br/>Loading Dose of: ' + load + ' mg IV in ' + loadvol + 'mL (3 mL/kg) of diluent over 60 min, then<br/>' + maint4hr + ' mg IV in ' + maint4hrvol + ' mL (7 mL/kg) of diluent over 4 hr then<br/>' + maint16hr + ' mg IV in ' + maint16hrvol + ' mL (14 mL/kg) of diluent over 16 hr.'
    }
};
calc_output.push({ 
    name: 'mini',
    value: load, 
    value_text: ' mg', 
    message: 'Loading dose' 
});
calc_output.push({ 
    name: '568_NAC Dosing_result', 
    value: ' ', 
    value_text: ' ', 
    message: msg 
});
