data.logic:
 var calc_output = [];
 var mini_msg = '';
 var msg = '';
 if (age == 0) {
     if (gcs_palpable_fracture_ams == 1) {
         mini_msg = 'CT Recommended';
         msg = 'PECARN recommends CT; 4.4% risk of clinically important Traumatic Brain Injury.'
     } else if (gcs_palpable_fracture_ams == 0) {
         if (hematoma_loc_injury == 1) {
             mini_msg = 'Observation Recommended';
             msg = 'PECARN recommends observation over imaging, depending on provider comfort; 0.9% risk of clinically important Traumatic Brain Injury.<br/><br/>Consider the following when making imaging decisions: Physician experience, worsening signs/symptoms during observation period, age <3 months, parent preference, multiple vs. isolated findings: patients with certain isolated findings (i.e., no other findings suggestive of TBI), such as isolated LOC, isolated headache, isolated vomiting, and certain types of isolated scalp hematomas in infants >3 months have ciTBI risk substantially <1%.'
         }
         if (hematoma_loc_injury == 0) {
             mini_msg = 'No Risk';
             msg = 'PECARN recommends No CT; Risk of ciTBI <0.02%, “Exceedingly Low, generally lower than risk of CT-induced malignancies.”'
         }
     }
 } else if (age == 1) {
     if (gcs_basilar_fracture_ams == 1) {
         mini_msg = 'CT Recommended';
         msg = 'PECARN recommends CT; 4.3% risk of clinically important Traumatic Brain Injury.'
     } else if (gcs_basilar_fracture_ams == 0) {
         if (loc_vomiting_headache_injury == 1) {
             mini_msg = 'Observation Recommended';
             msg = 'PECARN recommends observation over imaging, depending on provider comfort; 0.9% risk of clinically important Traumatic Brain Injury.<br/><br/>Consider the following when making imaging decisions: Physician experience, worsening signs/symptoms during observation period, age <3 months, parent preference, multiple vs. isolated findings: patients with certain isolated findings (i.e., no other findings suggestive of TBI), such as isolated LOC, isolated headache, isolated vomiting, and certain types of isolated scalp hematomas in infants >3 months have ciTBI risk substantially <1%.'
         } else if (loc_vomiting_headache_injury == 0) {
             mini_msg = 'No Risk';
             msg = 'PECARN recommends No CT; Risk <0.05%, \u201cExceedingly Low, generally lower than risk of CT-induced malignancies.\u201d'
         }
     }
 }

calc_output.push({ 
    name: 'mini', 
    value: ' ', 
    value_text: ' ', 
    message: mini_msg 
});
calc_output.push({ 
    name: '589_PECARN Pediatric Head Injury Rule_result', 
    value: ' ', 
    value_text: ' ', 
    message: msg 
});
