data.logic:
var calc_output = [];
var unit_symbol = '%';
var mini_msg = '';
var msg = '';

// units cancel each other out by division, so you're just left with a percentage. no unit conversion needed!

var value = (100 * (parseFloat(urine_sodium) * parseFloat(creatinine)) / (
  parseFloat((urine_creatinine) * parseFloat(sodium))));
if (value < 1) {
  mini_msg = 'Pre-Renal';
  msg =
    'e.g. Hypovolemia, heart failure, renal artery stenosis, sepsis (anything causing decreased effective renal perfusion). Remember, contrast-induced nephropathy will often look pre-renal.';
}
if (value >= 1 && value <= 4) {
  mini_msg = 'Intrinsic';
  msg = 'e.g. ATN, AIN, glomerulonephritides';
}
if (value > 4) {
  mini_msg = 'Post-Renal/Obstructive';
  msg = 'e.g. BPH, bladder stone, bilateral ureter obstruction';
}
calc_output.push({
  name: 'mini',
  value: value.toFixed(1),
  value_text: unit_symbol,
  message: mini_msg
});
calc_output.push({
  name: '60_FENa_result',
  value: value.toFixed(1),
  value_text: unit_symbol,
  message: 'FENa'
});
calc_output.push({
  name: '60_FENa_interpretation',
  value: mini_msg,
  value_text: '',
  message: msg
});
