data.logic:
var calc_output = []; 

var msg, mini_msg, txt; 

if (age + blood + seizure > 0) {
  mini_msg = 'Not eligible'
  msg = 'Not eligible'
  txt = 'Patient is not eligible for scoring because exclusion criteria met (see Instructions)'
}
else {
var score = glasgow_coma_score + fracture + basilar_fracture + vomiting + age_65; 
var score2 = amnesia + danger_mechanism; 


if (score2 > 0) {
  mini_msg = 'Consider'; 
  msg = 'Consider CT'; 
  txt= 'The Canadian Head CT Rule suggests a head CT is necessary for this patient as they are at \u201cmedium\u201d risk to rule out an intracranial traumatic finding (sensitivity 83-100%).'
} 
if (score > 0) {
  mini_msg = 'Consider'; 
  msg = 'Consider CT'; 
  txt='The Canadian Head CT Rule cannot rule out need for imaging. Consider CT.'
} if (score == 0 && score2 == 0) {
  mini_msg = 'Unnecessary'; 
  msg = 'CT Unnecessary'; 
  txt='The Canadian Head CT Rule suggests a head CT is not necessary for this patient (sensitivity 83-100% for all intracranial traumatic findings, sensitivity 100% for findings requiring neurosurgical intervention).'} 
}

  calc_output.push({
    name: 'mini', 
    value: mini_msg, 
    value_text: ' ', 
    message: 'CT for Head Trauma.'
  }); 
  calc_output.push({
    name: '608_Canadian CT Head Rule_result', 
    value: msg, 
    value_text: ' ', 
    message: txt 
  });
