data.logic:
var calc_output=[]; 

var msg=''; 
var mini_msg='';

var v=parseInt(signs_symptoms); 
if(v==1){
    msg='Grade I Hepatic Encephalopathy';
    mini_msg='Grade I'
} else if(v==2){
    msg='Grade II Hepatic Encephalopathy';
    mini_msg='Grade II'
} else if(v==3){
    msg='Grade III Hepatic Encephalopathy';
    mini_msg='Grade III'
} else if(v==4){
    msg='Grade IV Hepatic Encephalopathy';
    mini_msg='Grade IV'
}

calc_output.push({
    name:'mini',
    value:v.toFixed(0),
    value_text:'points',
    message:mini_msg
}); 
calc_output.push({
    name:'674_Hepatic Encephalopathy Grades_result',
    value:v.toFixed(0),
    value_text:'points',
    message:msg
});
