data.logic:
var calc_output = [];
var u = parseFloat(chest_wall_retractions) + parseFloat(stridor) + parseFloat(cyanosis) + parseFloat(consciousness) + parseFloat(air_entry);
var msg = '';
var mini_msg = '';
var msgalt = '';
if (u > 11) {
    msg = 'Scores of 12 or greater indicated impending respiratory failure! Get to the bedside, the child may require intubation!';
    mini_msg = 'Impending respiratory failure'
}
if (u < 12 && u > 5) {
    msg = 'Severe croup; admission is recommended.';
    mini_msg = 'Severe croup'
}
if (u < 6 && u > 2) {
    msg = 'Moderate croup severity.';
    mini_msg = 'Moderate croup severity'
}
if (u < 3) {
    msg = 'Mild croup severity.';
    mini_msg = 'Mild croup severity'
}
if (u <= 17 && u >= 0) {
    msgalt = 'Remember that the Westley Croup Score is typically used for research purposes; management is typically based on clinical signs and symptoms: (1) Consider bacterial tracheitis in an ill-appearing child with croup-like symptoms. (2) The vast majority (>80%) of children presenting to the ED with croup have mild disease; severe croup is rare (<1%).'
};
calc_output.push({
    name: 'mini',
    value: u.toFixed(0),
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '677_Westley Croup Score_result',
    value: u.toFixed(0),
    value_text: 'points',
    message: msg+'<br/><br/>'+msgalt
});
