data.logic:
var calc_output = [];
var u, v, p, q, units;
var ht;
var abw = parseFloat(abw);
var height = parseFloat(height)/2.54;
//needs immediate conversion from cm to inches for ht variable, and toggle units are always cm
if (height > 60) {
    ht = height - 60;
} else {
    ht = 0;
}
if (parseFloat(sex) == 1) {
    v = 45.5 + (ht * 2.3);
} else {
    v = 50 + (ht * 2.3);
}
if (parseFloat(sex) == 1) {
    p = 2.20462 * (45.5 + (ht * 2.3));
} else {
    p = 2.20462 * (50 + (ht * 2.3));
}

var ABW, q_2, ABW_2, secunit_q, secunit_abw;

ABW = v + 0.4*(abw - v);

if (UOMSYSTEM == false){
    q = v;
  	abw = abw;
    ABW = ABW;
    units = 'kg';
    q_2 = v*2.20462;
    ABW_2 = ABW*2.20462;
    secunit_q = '<br/><br/>Equivalent to ' + q_2.toFixed(0) + ' lbs';
    secunit_abw = '<br/><br/>Equivalent to ' + ABW_2.toFixed(0) + ' lbs';
}
if (UOMSYSTEM == true){
    q = p;
  	ABW = ABW*2.20462;
  	abw = abw*2.20462;
    units = 'lbs';
    q_2 = p*0.453592;
    ABW_2 = ABW*0.453592;
    secunit_q = '<br/><br/>Equivalent to ' + q_2.toFixed(0) + ' kg';
    secunit_abw = '<br/><br/>Equivalent to ' + ABW_2.toFixed(0) + ' kg';
}

if (abw > 0) {
  var ratio = abw.toFixed(0)/q.toFixed(0);
  var ratio_perc = ratio*100;
  ratio = ratio.toFixed(1);
  ratio_perc = ratio_perc.toFixed(0);
  if (abw.toFixed(0) == q.toFixed(0)) {
    var msg = '<br/><br/>Actual body weight is equal to ideal body weight';
  } else {
    var msg = '<br/><br/>Actual body weight is '+ratio_perc+'% ('+ratio+'x) ideal body weight';
  }
} else {
  var msg = '';
}

calc_output.push({
    name: 'mini',
    value: q.toFixed(0),
    value_text: units,
    message: 'Ideal Body Weight'
});
calc_output.push({
    name: '68_IBW & ABW_result (IBW)',
    value: q.toFixed(0),
    value_text: units,
    message: 'Ideal Body Weight'+ secunit_q + msg
});
if (abw > q.toFixed(0)){
calc_output.push({
    name: '68_IBW & ABW_result (ABW)',
    value: ABW.toFixed(0),
    value_text: units,
    message: 'Adjusted Body Weight'+ secunit_abw
});
}
if (abw > 0 && abw <= q.toFixed(0)){
calc_output.push({
    name: '68_IBW & ABW_result (ABW)',
    value: '',
    value_text: '',
    message: 'Adjusted body weight only applicable if actual body weight is greater than ideal body weight'
});
}
